import logo from './logo.svg';
import './App.css';
import Keyboard from './components/Keyboard'

function App() {

  document.addEventListener('keydown', function(event) {
    event.stopImmediatePropagation();

    let keys = "qwertyuiopasdfghjklzxcvbnm".split('');
    for (let i=0; i<keys.length; i++) {
      if (event.key === keys[i]) {
        let selectedElement = document.getElementById(keys[i]);
        selectedElement.className = "key-letter-pressed";
      }
    }

    let keyNumbers = "1234567890".split('');
    for (let i=0; i<keyNumbers.length; i++) {
      if (event.key === keyNumbers[i]) {
        let selectedElement = document.getElementById("key" + keyNumbers[i]);
        selectedElement.className = "key-number-pressed";
      }
    }

    if (event.key === "Shift") {
      let toShow = document.getElementsByClassName("key-number-alt");
      for (let i=0; i<toShow.length; i++) {
        toShow[i].hidden = false;
      }

      let toHide = document.getElementsByClassName("key-number");
      for (let i=0; i<toHide.length; i++) {
        toHide[i].hidden = true;
      }
    }
  });

  document.addEventListener('keyup', function(event) {
    event.stopImmediatePropagation();
    let keys = "qwertyuiopasdfghjklzxcvbnm".split('');
    for (let i=0; i<keys.length; i++) {
      if (event.key === keys[i]) {
        let selectedElement = document.getElementById(keys[i]);
        selectedElement.className = "key-letter";
      }
    }

    let keyNumbers = "1234567890".split('');
    for (let i=0; i<keyNumbers.length; i++) {
      if (event.key === keyNumbers[i]) {
        let selectedElement = document.getElementById("key" + keyNumbers[i]);
        selectedElement.className = "key-number";
      }
    }

    if (event.key === "Shift") {
      let toShow = document.getElementsByClassName("key-number-alt");
      for (let i=0; i<toShow.length; i++) {
        toShow[i].hidden = true;
      }

      let toHide = document.getElementsByClassName("key-number");
      for (let i=0; i<toHide.length; i++) {
        toHide[i].hidden = false;
      }
    }
  })

  return (
    <div className="grid">

      <div className="row-1">
        <div className="key-double"></div>
        <div className="key-number" id="key1">1</div>
        <div className="key-number-alt" id="keyExclamation" hidden={true}>!</div>
        <div className="key-number" id="key2">2</div>
        <div className="key-number-alt" id="keyAt" hidden={true}>@</div>
        <div className="key-number" id="key3">3</div>
        <div className="key-number-alt" id="keyPound" hidden={true}>#</div>
        <div className="key-number" id="key4">4</div>
        <div className="key-number-alt" id="keyDollar" hidden={true}>$</div>
        <div className="key-number" id="key5">5</div>
        <div className="key-number-alt" id="keyPercent" hidden={true}>%</div>
        <div className="key-number" id="key6">6</div>
        <div className="key-number-alt" id="keyCarrot" hidden={true}>^</div>
        <div className="key-number" id="key7">7</div>
        <div className="key-number-alt" id="keyAnd" hidden={true}>&</div>
        <div className="key-number" id="key8">8</div>
        <div className="key-number-alt" id="keyAsterisk" hidden={true}>*</div>
        <div className="key-number" id="key9">9</div>
        <div className="key-number-alt" id="keyOpenParens" hidden={true}>(</div>
        <div className="key-number" id="key0">0</div>
        <div className="key-number-alt" id="keyCloseParens" hidden={true}>)</div>
        <div className="key-double"></div>
        <div className="key-double"></div>
        <div className="key-three"></div>
      </div>

